import React, { useState, useRef } from 'react';
import './home.css';

const WelcomePage = ({ name }) => {
  return (
    <div className="welcome-container">
      <h2 className="welcome-heading">Welcome, {name}!</h2>
      <p className="welcome-message">
        We're glad you're here, {name}. Get ready for some fun and challenging quizzes!
      </p>
      {/* Add optional content like links to quizzes or instructions */}
      <button className="welcome-button">Start Quizzing</button>
    </div>
  );
};

const Home = () => {
  const formRef = useRef(null);
  const [name, setName] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);

  const handleChange = (event) => {
    setName(event.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (name.trim() !== '') {
      setLoggedIn(true);
    } else {
      alert("Please enter your name.");
    }
  };

  return (
    <>
      <main className="flex-1">
        <section className="w-full py-6 md:py-12 lg:py-16 xl:py-24">
          <div className="container flex flex-col items-center justify-center space-y-4 px-4 md:px-6 text-center">
            <div className="space-y-2">
              <h1 className="text-4xl font-bold tracking-tighter sm:text-5xl md:text-6xl">Quiz Time</h1>
              <p className="mx-auto max-w-[600px] text-gray-500 md:text-xl/relaxed lg:text-base/relaxed xl:text-xl/relaxed dark:text-gray-400">
                Test your knowledge with our fun and challenging quizzes. Play solo or challenge your friends.
              </p>
              <form ref={formRef} onSubmit={handleSubmit}>
                <input
                  type="text"
                  placeholder="Enter your Name"
                  name="text"
                  value={name}
                  onChange={handleChange}
                  className="input"
                />
                <button type="submit" className="btn">Submit</button>
              </form>
            </div>
          </div>
        </section>
      </main>

      {/* Conditionally render WelcomePage based on loggedIn state */}
      {loggedIn && <WelcomePage name={name} />}
    </>
  );
};

export default Home;
