import React, { useState, useRef } from 'react';
import { useEffect } from 'react';
import './home.css';

const Home = () => {
  const formRef = useRef(null); // useRef to get reference to the form element

  const [name, setName] = useState('');

  const handleChange = (event) => {
    setName(event.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Programmatically submit the form
    formRef.current.submit();
  };

  useEffect(() => {
    if (name !== '') {
      console.log("User is logged in");
    }
  }, [name]);

  return (
    <>
      <main className="flex-1">
        <section className="w-full py-6 md:py-12 lg:py-16 xl:py-24">
          <div className="container flex flex-col items-center justify-center space-y-4 px-4 md:px-6 text-center">
            <div className="space-y-2">
              <h1 className="text-4xl font-bold tracking-tighter sm:text-5xl md:text-6xl">Quiz Time</h1>
              <p className="mx-auto max-w-[600px] text-gray-500 md:text-xl/relaxed lg:text-base/relaxed xl:text-xl/relaxed dark:text-gray-400">
                Test your knowledge with our fun and challenging quizzes. Play solo or challenge your friends.
              </p>
              <form
                ref={formRef} // Assign the ref to the form element
                onSubmit={(e) => e.preventDefault()} // Prevent default form submission behavior
              >
                <input
                  type="text"
                  placeholder="Enter your Name"
                  name="text"
                  value={name}
                  onChange={handleChange}
                  className="input"
                />
                <button type="button" onClick={handleSubmit} className="btn">Submit</button>
              </form>
            </div>
          </div>
        </section>
      </main>
    </>
  );
};

export default Home;
