import React, { useState, useRef, useEffect } from 'react';
import './home.css';

const Home = () => {
  const formRef = useRef(null);
  const [name, setName] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await fetch('questions.json'); // Assuming questions.json is in the same directory
        const data = await response.json();
        setQuestions(data.questions);
      } catch (error) {
        console.error('Error fetching questions:', error);
      }
    };

    fetchQuestions();
  }, []);

  const handleChange = (event) => {
    setName(event.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (name.trim() !== '') { 
      console.log("User is logged in");
      setLoggedIn(true); 
    } else {
      alert("Please enter your name."); 
    }
  };

  const handlePrevious = () => {
    setCurrentQuestionIndex((prevIndex) => prevIndex - 1);
  };

  const handleNext = () => {
    setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
  };

  if (loggedIn) {
    // Redirect user to the welcome page if logged in
    return <WelcomePage name={name} />;
  }

  return (
    <>
      <main className="flex-1">
        <section className="w-full py-6 md:py-12 lg:py-16 xl:py-24">
          <div className="container flex flex-col items-center justify-center space-y-4 px-4 md:px-6 text-center">
            <div className="space-y-2">
              <h1 className="text-4xl font-bold tracking-tighter sm:text-5xl md:text-6xl">Quiz Time</h1>
              <p className="mx-auto max-w-[600px] text-gray-500 md:text-xl/relaxed lg:text-base/relaxed xl:text-xl/relaxed dark:text-gray-400">
                Test your knowledge with our fun and challenging quizzes. Play solo or challenge your friends.
              </p>
              <form
                ref={formRef}
                onSubmit={(e) => e.preventDefault()}
              >
                <input
                  type="text"
                  placeholder="Enter your Name"
                  name="text"
                  value={name}
                  onChange={handleChange}
                  className="input"
                />
                <button type="submit" onClick={handleSubmit} className="btn">Submit</button>
              </form>
            </div>
            {loggedIn && questions.length > 0 && (
              <Question
                question={questions[currentQuestionIndex]}
                onPrevious={handlePrevious}
                onNext={handleNext}
                isFirst={currentQuestionIndex === 0}
                isLast={currentQuestionIndex === questions.length - 1}
              />
            )}
          </div>
        </section>
      </main>
    </>
  );
};

const Question = ({ question, onPrevious, onNext, isFirst, isLast }) => {
  return (
    <div className="question-container">
      <h2 className="question">{question.question}</h2>
      <ul className="options">
        {question.options.map((option, index) => (
          <li key={index}>{option}</li>
        ))}
      </ul>
      <div className="navigation-buttons">
        <button onClick={onPrevious} disabled={isFirst}>Previous</button>
        <button onClick={onNext} disabled={isLast}>Next</button>
      </div>
    </div>
  );
};

const WelcomePage = ({ name }) => {
  return (
    <>
      <div className="welcome-container">
        <h2 className="welcome-heading">Welcome, {name}!</h2>
      </div>
    </> 
  );
};

export default Home;
